import React from 'react';
import { connect } from 'react-redux';

const StepTracker = ({initiativeStep, handleIncrementClick, handleDecrementClick }) => (
  <div>
    <h1>Initiative Step Tracker {initiativeStep}</h1>
    <button onClick={handleDecrementClick}>Decrement</button>
    <button onClick={handleIncrementClick}>Increment</button>
  </div>
);

const mapStateToProps = state => {
  return {
    initiativeStep: state
  };
};

const mapDispatchToProps = dispatch => {
  return {
    handleIncrementClick: () => dispatch({ type: 'INCREMENT' }),
    handleDecrementClick: () => dispatch({ type: 'DECREMENT' })
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(StepTracker);
